import enum


class ServiceTypeEnum(str, enum.Enum):
    cdn = 'cdn'
    cms = 'cms'
    crm = 'crm'
    dns = 'dns'
    email_vendor = 'email_vendor'
    paas = 'paas'

    @classmethod
    def has_key(cls, name):
        return name in cls.__members__


